let counter = 0;

function updateCounter() {
    document.getElementById("counter").textContent = counter;
}

const intervalId = setInterval(() => {
    counter++;
    updateCounter();
}, 1000);

const plusBtn = document.getElementById("plus");
const minusBtn = document.getElementById("minus");

plusBtn.addEventListener("click", () => {
    counter++;
    updateCounter();
});

minusBtn.addEventListener("click", () => {
    if (counter > 0) {
        counter--;
        updateCounter();
    }
});

const likesList = document.querySelector("ul.likes");
let likes = {};

function addLike() {
    const currentLikes = likes[counter] || 0;
    likes[counter] = currentLikes + 1;

    const likeItem = document.createElement("li");
    likeItem.textContent = `❤️ Liked at ${counter}: ${likes[counter]} likes`;
    likesList.appendChild(likeItem);
}

// Handle "like" button click (assuming it's not provided)
// Add event listener to the "heart" button and call addLike

const pauseBtn = document.getElementById("pause");

let isPaused = false;

pauseBtn.addEventListener("click", () => {
    isPaused = !isPaused;

    if (isPaused) {
        clearInterval(intervalId);
        pauseBtn.textContent = "Resume";
        plusBtn.disabled = true;
        minusBtn.disabled = true;
    } else {
        intervalId = setInterval(updateCounter, 1000);
        pauseBtn.textContent = "Pause";
        plusBtn.disabled = false;
        minusBtn.disabled = false;
    }
});

